mapper.select:
    SELECT count(*) totalCount
    FROM TB_BOARD


	    	AND NO = #{no}


	    	AND TITLE = #{title}


	    	AND CONTENTS = #{contents}


	    	AND RGST_ID = #{rgstId}

    SELECT *
    		,DATE_FORMAT(RGST_DT, '%Y-%m-%d %H:%i:%s') FM_RGST_DT 
    FROM TB_BOARD


	    	AND NO = #{no}


	    	AND PW = #{pw}


	    	AND TITLE = #{title}


	    	AND CONTENTS = #{contents}


	    	AND PW = #{pw}


	    	AND RGST_ID = #{rgstId}


    ORDER BY RGST_DT DESC

   		limit #{limit1}, #{limit2}

mapper.update:
UPDATE TB_BOARD
SET TITLE = #{title}, CONTENTS = #{contents}, UPDT_DT = now()  
WHERE NO = #{no}
